import React, {useState, useEffect} from 'react';
import axios from 'axios';
import {Switch, Route, BrowserRouter, Redirect} from 'react-router-dom';

import useToken from './token.js';
import Newsfeed from './components/newsfeed/news-feed.jsx';
import LandingPage from './components/landing/landing-page.jsx';
// import  AddEmployee from './components/employee/add-employee.jsx';
import './components/newsfeed/newsfeed.css';
import 'bootstrap/dist/css/bootstrap.css';
import 'font-awesome/css/font-awesome.css';

const App = () => {

  const { token, setToken } = useToken();

  // if(!token) {
  //   return <LandingPage isSignIn={true}/>
  // }

  return (
      <div  className="theme-layout">
          <BrowserRouter>
            <Switch>
              {token ? <Route exact path='/'  render={() => <Newsfeed /> }/> :  <Route path='/login'  render={() => <LandingPage isSignIn={true}/> }/>}
              {/* {/* <Route path='/'  render={() =>  <EmployeeList /> }/>  */}
              <Route exact path='/'  render={() => <Newsfeed /> }/>
              <Route path='/login'  render={() => <LandingPage isSignIn={true}/> }/>
              <Route path='/register'  render={() => <LandingPage isRegister={true}/> }/>
              {/* <Route path='/signin'  render={() => <Login /> }/>  */}
            </Switch>
          </BrowserRouter>
    </div>
  )
};

export default App;
