const getToken = () => {
    const tokenString = localStorage.getItem('token');
    // const userToken = JSON.parse(tokenString);
    return tokenString
  };

const saveToken = userToken => {
    localStorage.setItem('token', userToken);
};

const resetToken = () => {
    localStorage.removeItem('token');
    return true;
  };

module.exports = {
    setToken: saveToken,
    token,
    resetToken
}
