import React, {useState} from 'react';
import axios from 'axios';

import {Link} from 'react-router-dom';
import FormInput from '../common-component/form-input.jsx';

const Register = () => {

    const [userName, setUserName] = useState('');
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [gender, setGender] = useState('');

    const handleSubmit = async event => {
        if(!(password || email || userName)) {
            return false;
        }

        let userData = {
            userName,
            name,
            password,
            email,
            gender
        }

        console.log("userData: ", userData);
        await axios({
            method: "POST",
            url: '/registeras',
            data: userData
        }).then(res => {

            console.log("res", res);

        }).catch(e => {
            console.log("Error occurred: ", e);
        })
    };
    const handleChange = (event, type) => {
        let value = event?.target?.value || "";

        if(type === 'name')  setName(value);
        if(type === 'userName')  setUserName(value);
        if(type === 'password')  setPassword(value);
        if(type === 'email')  setEmail(value);
        if(type === 'gender')  setGender(value);

    };

    return (
        <div className="log-reg-area reg">
            <h2 className="log-title">Register</h2>
            <form method="post">
                <FormInput type={'text'} value={name} handleChange= {e => handleChange(e, 'name')} label={'First & Last Name'}/>
                {/* <div className="form-group">	
                <input type="text" required="required"/>
                <label className="control-label" htmlFor="input">First & Last Name</label><i className="mtrl-select"></i>
                </div> */}

                <FormInput type={'text'} value={userName} handleChange= {e => handleChange(e, 'userName')} label={'User Name'}/>
               
               {/* <div className="form-group">	
                <input type="text" required="required"/>
                <label className="control-label" htmlFor="input">User Name</label><i className="mtrl-select"></i>
                </div> */}
                <FormInput type={'password'} value={password} handleChange= {e =>  handleChange(e, 'password')} label={'Password'}/>
                
                {/* <div className="form-group">	
                <input type="password" required="required"/>
                <label className="control-label" htmlFor="input">Password</label><i className="mtrl-select"></i>
                </div> */
                }
                <div className="form-radio">
                    <div className="radio">
                        <label>
                        <input type="radio" name="radio" onChange = {e =>  handleChange(e, 'gender')} checked="checked"/><i className="check-box"></i>Male
                        </label>
                    </div>
                    <div className="radio">
                        <label>
                            <input type="radio" name="radio"/><i className="check-box"></i>Female
                        </label>
                    </div>
                </div>
                <FormInput type={'text'} value={email} onChange= {e =>  handleChange(e, 'email')} label={'Email'}/>
                
                {/* <div className="form-group">	
                    <input type="text" required="required"/>
                    <label className="control-label" htmlFor="input"><a href="https://wpkixx.com/cdn-cgi/l/email-protection" className="__cf_email__" data-cfemail="6c29010d05002c">Email</a></label><i className="mtrl-select"></i>
                </div> */}

                <div className="checkbox">
                <label>
                    <input type="checkbox" onChange={e => e.target.value} checked="checked"/><i className="check-box"></i>Accept Terms & Conditions ?
                </label>
                </div>
                <Link to="/login" className="already-have">Already have an account</Link>
                <div className="submit-btns">
                    <button className="mtr-btn signup" onClick = {e => handleSubmit() }type="button"><span>Register</span></button>
                </div>
            </form>
        </div>
    )
}

export default Register;
