import { createSelector} from 'reselect';

const selectUser = state => state.user;

export const selectUserLoggedIn = createSelector(
    [selectUser],  user =>user.isLoggedIn
);
export const selectLoggedOut = createSelector(
    [selectUser],  user =>   user.isLoggedIn
);

