import React, {useState} from 'react';
import axios from 'axios';

const AddComment = () => {
    const [comment, setComment] = userState()

    const handleChange = event => {
        let value = event?.target?.value;

        if(value) setComment(value);
    }
    const handleSubmit = async event => {
        if(!comment) return false;

        await axios({
            method: "POST",
            url: 'http://localhost:4000/post/comments',
            headers: {
               "Access-Control-Allow-Origin": "*",
               'Content-Type': 'application/json',
               token
            },
           data: {
               description: comment,
               commentedAt: new Date()
           }
        }).then(res => {
            if(res.data.comments) {
                let comments = res.data.comments;
                
                setComments(comments);
            }
        }).catch(e => {
            console.log("Error occurred: ", e);
        })

    }

    return(
        <>
            <li className="post-comment">
                <div className="comet-avatar">
                    <img src="images/resources/comet-1.jpg" alt="" />
                </div>
                <div className="post-comt-box">
                    <div>
                        <textarea placeholder="Post your comment" value={comment} onChange={e => handleChange(e)}></textarea>
                        <button type="submit" onSubmit={e=> handleSubmit(e)}></button>
                    </div>	
                </div>
            </li>
        </>
    )
}

export default AddComment;
